const FRONT_BODY_PATHS = []; // Махаме контурите за по-изчистено
const BACK_BODY_PATHS = [];
const FEMALE_FRONT_BODY_PATHS = []; 
const FEMALE_BACK_BODY_PATHS = [];


// --- 3. ГЛОБАЛНИ ПРОМЕНЛИВИ (Включително за Пол) ---
let currentView = 'front';
let currentGender = 'male'; // НОВО: Пази дали е Мъж или Жена
let selectedMuscleId = null;
let currentUserRole = null;


// --- 4. АВТЕНТИКАЦИЯ ---
onAuthStateChanged(auth, async (user) => {
    const loginLi = document.getElementById('login-li');
    const logoutLi = document.getElementById('logout-li');
    const userDisplay = document.getElementById('user-display');
    const logoutButton = document.getElementById('logout-btn');

    if (user) {
        if(loginLi) loginLi.style.display = 'none';
        if(logoutLi) logoutLi.style.display = 'block';
        if(userDisplay) {
            userDisplay.style.display = 'block';
            userDisplay.innerText = user.email.split('@')[0];
        }

        try {
            const userDoc = await getDoc(doc(db, "users", user.uid));
            if (userDoc.exists()) {
                currentUserRole = userDoc.data().role;
            }
        } catch (e) { console.error("Error checking role:", e); }

    } else {
        currentUserRole = null;
        if(loginLi) loginLi.style.display = 'block';
        if(logoutLi) logoutLi.style.display = 'none';
        if(userDisplay) userDisplay.style.display = 'none';
    }

    if (logoutButton) {
        logoutButton.onclick = () => {
            signOut(auth).then(() => {
                alert("Logged out!");
                window.location.reload();
            });
        };
    }
});


// --- 5. ЛОГИКА ЗА МОДЕЛА (СЕРЦЕТО НА ПРОГРАМАТА) ---
function renderModel() {
    const svg = document.getElementById('human-svg');
    svg.innerHTML = ""; // Изчистваме всичко старо (Оправя проблема с градиента)

    let musclesData, bodyPaths;

    // ИЗБИРАМЕ ПРАВИЛНИЯ МАСИВ СПРЯМО ПОЛА И ИЗГЛЕДА
    if (currentGender === 'male') {
        if (currentView === 'front') {
            musclesData = FRONT_MUSCLES;
            bodyPaths = FRONT_BODY_PATHS;
        } else {
            musclesData = BACK_MUSCLES;
            bodyPaths = BACK_BODY_PATHS;
        }
    } else {
        if (currentView === 'front') {
            musclesData = FEMALE_FRONT_MUSCLES;
            bodyPaths = FEMALE_FRONT_BODY_PATHS;
        } else {
            musclesData = FEMALE_BACK_MUSCLES;
            bodyPaths = FEMALE_BACK_BODY_PATHS;
        }
    }

    // РИСУВАНЕ НА МУСКУЛИТЕ
    musclesData.forEach(muscle => {
        const group = document.createElementNS("http://www.w3.org/2000/svg", "g");
        group.id = muscle.id;
        group.classList.add('muscle-group');

        if(!muscle.paths) return;

        muscle.paths.forEach(d => {
            const path = document.createElementNS("http://www.w3.org/2000/svg", "path");
            path.setAttribute("d", d);
            path.classList.add('muscle-path');
            
            // Клик събитие (Изпраща към exercises.html)
            path.addEventListener('click', (e) => {
                e.stopPropagation();
                handleMuscleClick(muscle.id, muscle.name);
            });

            // Ховър ефекти
            path.addEventListener('mouseenter', () => {
                 document.getElementById('hover-label').innerText = muscle.name;
                 path.style.fill = "#ff4757"; // Highlight
            });
            path.addEventListener('mouseleave', () => {
                 path.style.fill = ""; 
            });

            group.appendChild(path);
        });
        svg.appendChild(group);
    });

    // Рисуване на контура (ако има такъв)
    const bodyGroup = document.createElementNS("http://www.w3.org/2000/svg", "g");
    bodyGroup.setAttribute("stroke", "#484a68");
    bodyGroup.setAttribute("fill", "none");
    bodyGroup.setAttribute("stroke-width", "3");
    
    if(bodyPaths) {
        bodyPaths.forEach(d => {
            const path = document.createElementNS("http://www.w3.org/2000/svg", "path");
            path.setAttribute("d", d);
            bodyGroup.appendChild(path);
        });
    }
    svg.appendChild(bodyGroup);
}


